sprites.onOverlap(SpriteKind.hitbox, SpriteKind.Enemy, function (sprite, otherSprite) {
    hitbox2.startEffect(effects.coolRadial, 1000)
    info.changeLifeBy(-1)
    movimiento = 0
    pause(100)
    if (sprite.x < otherSprite.x) {
        hitbox2.vy += -200
        hitbox2.vx += -100
    }
    if (sprite.x > otherSprite.x) {
        hitbox2.vy += -200
        hitbox2.vx += 100
    }
    pause(200)
    pauseUntil(() => hitbox2.isHittingTile(CollisionDirection.Bottom))
    hitbox2.ax = 0
    hitbox2.vx = 0
    pause(100)
    movimiento = 1
    pause(1000)
})
info.onLifeZero(function () {
    sprites.destroyAllSpritesOfKind(SpriteKind.Player)
    sprites.destroyAllSpritesOfKind(SpriteKind.Background)
    sprites.destroyAllSpritesOfKind(SpriteKind.hitbox)
    sprites.destroyAllSpritesOfKind(SpriteKind.sombra)
    sprites.destroyAllSpritesOfKind(SpriteKind.score)
    sprites.destroyAllSpritesOfKind(SpriteKind.sombar)
    sprites.destroyAllSpritesOfKind(SpriteKind.premio)
    sprites.destroyAllSpritesOfKind(SpriteKind.cofre)
    sprites.destroyAllSpritesOfKind(SpriteKind.Projectile)
    sprites.destroyAllSpritesOfKind(SpriteKind.Food)
    sprites.destroyAllSpritesOfKind(SpriteKind.Enemy)
    sprites.destroyAllSpritesOfKind(SpriteKind.AffectedByPhysics)
    sprites.destroyAllSpritesOfKind(SpriteKind.StatusBar)
    sprites.destroyAllSpritesOfKind(SpriteKind.platform)
    sprites.destroyAllSpritesOfKind(SpriteKind.abierto)
    sprites.destroyAllSpritesOfKind(SpriteKind.platform)
    sprites.destroyAllSpritesOfKind(SpriteKind.bounce)
    player2.stop()
    extran += -1
    pause(1000)
    if (extran == 5) {
        textSprite.setText("x5")
        level0()
        timer.background(function () {
            pause(2000)
            level1()
        })
    } else if (extran == 4) {
        textSprite.setText("x4")
        level0()
        timer.background(function () {
            pause(2000)
            level1()
        })
    } else if (extran == 3) {
        textSprite.setText("x3")
        level0()
        timer.background(function () {
            pause(2000)
            level1()
        })
    } else if (extran == 2) {
        textSprite.setText("x2")
        level0()
        timer.background(function () {
            pause(2000)
            level1()
        })
    } else if (extran == 1) {
        textSprite.setText("x1")
        level0()
        timer.background(function () {
            pause(2000)
            level1()
        })
    } else if (extran == 0) {
        textSprite.setText("x0")
        level0()
        timer.background(function () {
            pause(2000)
            level1()
        })
    } else if (extran < 0) {
        level0()
    }
})
