sprites.onOverlap(SpriteKind.hitbox, SpriteKind.score, function (sprite, otherSprite) {
    music.play(music.melodyPlayable(music.baDing), music.PlaybackMode.InBackground)
    sprites.destroy(otherSprite)
    info.changeScoreBy(2)
})
controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    pauseUntil(() => pausa == true)
    if (libreS == 1) {
        libreS = 0
        if (hitbox2.isHittingTile(CollisionDirection.Bottom) == true && controller.down.isPressed() == false || hitbox2.overlapsWith(movingplat) == true && controller.down.isPressed() == false) {
            salto = 0
            caida = 0
            hitbox2.vy = -250
            music.play(music.createSoundEffect(WaveShape.Square, 1, 1043, 255, 0, 500, SoundExpressionEffect.None, InterpolationCurve.Curve), music.PlaybackMode.InBackground)
            for (let index = 0; index < 4; index++) {
                pause(15)
                if (controller.A.isPressed() == true) {
                    hitbox2.vy += 5
                } else {
                    hitbox2.vy += 50
                }
            }
            salto = 1
            pause(100)
            caida = 1
        }
    }
})
controller.down.onEvent(ControllerButtonEvent.Released, function () {
    pauseUntil(() => pausa == true)
    agachado = 0
})
controller.left.onEvent(ControllerButtonEvent.Pressed, function () {
    pauseUntil(() => pausa == true)
    Dirección = 1
})
sprites.onCreated(SpriteKind.hitbox, function (sprite) {
    info.setLife(4)
})
scene.onOverlapTile(SpriteKind.hitbox, sprites.builtin.forestTiles14, function (sprite, location) {
    pauseUntil(() => pausa == true)
    muerto = 1
    hitbox2.setFlag(SpriteFlag.Ghost, true)
    movimiento = 0
    player2.stop()
    timer.background(function () {
        music.play(music.createSoundEffect(WaveShape.Sine, 776, 1, 255, 0, 1000, SoundExpressionEffect.Warble, InterpolationCurve.Linear), music.PlaybackMode.InBackground)
        pause(1000)
        color.startFadeFromCurrent(color.Black, 500)
        pause(1000)
        info.setLife(0)
    })
})
