sprites.onOverlap(SpriteKind.hitbox, SpriteKind.Jefe, function (sprite, otherSprite) {
    hitbox2.startEffect(effects.coolRadial, 1000)
    info.changeLifeBy(-1)
    if (info.life() == 0) {
        muerto = 1
    }
    movimiento = 0
    pause(100)
    if (sprite.x < otherSprite.x) {
        hitbox2.vy += -200
        hitbox2.vx += -100
    }
    if (sprite.x > otherSprite.x) {
        hitbox2.vy += -200
        hitbox2.vx += 100
    }
    pause(200)
    pauseUntil(() => hitbox2.isHittingTile(CollisionDirection.Bottom))
    hitbox2.ax = 0
    hitbox2.vx = 0
    pause(100)
    movimiento = 1
    pause(1000)
})
function jefe () {
    statusbar = statusbars.create(200, 20, StatusBarKind.EnemyHealth)
    statusbar.value = 100
    statusbar.positionDirection(CollisionDirection.Bottom)
    statusbar.setOffsetPadding(0, 10)
    statusbar.setColor(2, 12, 5)
    statusbar.setStatusBarFlag(StatusBarFlag.SmoothTransition, true)
    statusbar.setBarBorder(2, 15)
}
sprites.onOverlap(SpriteKind.platform, SpriteKind.bounce, function (sprite, otherSprite) {
    sprite.vx = sprite.vx * -1
    pause(500)
})
