sprites.onOverlap(SpriteKind.Enemy, SpriteKind.Player, function (sprite, otherSprite) {
    pauseUntil(() => pausa == true)
    if (ataque == 1) {
        if (sprites.readDataNumber(sprite, "health") <= 0) {
            sprites.destroy(sprite, effects.ashes, 100)
            info.changeScoreBy(10)
        } else {
            sprite.startEffect(effects.coolRadial, 1000)
            sprites.setDataNumber(sprite, "health", sprites.readDataNumber(sprite, "health") - 1)
            pause(500)
        }
    }
})
