forever(function () {
    if (info.score() >= 100) {
        info.setScore(info.score() - 100)
        extran += 1
        if (extran == 5) {
            textSprite.setText("x5")
        } else if (extran == 4) {
            textSprite.setText("x4")
        } else if (extran == 3) {
            textSprite.setText("x3")
        } else if (extran == 2) {
            textSprite.setText("x2")
        } else if (extran == 1) {
            textSprite.setText("x1")
        }
    }
})
